mapper.select:
SELECT *
FROM dealInfo
WHERE dongName = #{dongName}

    ORDER BY dealYear ${sortOrder}, dealMonth ${sortOrder}, dealDay ${sortOrder}


    ORDER BY ${sortBy} ${sortOrder}

select *
from dealInfo
where aptName = #{aptName}

    ORDER BY dealYear ${sortOrder}, dealMonth ${sortOrder}, dealDay ${sortOrder}


    ORDER BY ${sortBy} ${sortOrder}
