mapper.update:
update user
set password = #{password},
    name     = #{name},
    email    = #{email},
    phone    = #{phone}
where id = #{id}

mapper.select:
select password
from user
where id = #{id}
  and name = #{name}
  and phone = #{phone}

select password
from user
where id = #{id}
  and name = #{name}
  and email = #{email}
